      SUBROUTINE HWDHO1(IHEP,ID,IM,NPR,LHEP,MHEP)
C-----------------------------------------------------------------------
C   Subroutine to perform the first part of the heavy object decays
C   IE to select the decay mode
C   was part of HWDHOB
C-----------------------------------------------------------------------
      INCLUDE 'herwig6500.inc'
      DOUBLE PRECISION HWUMBW,HWRGEN,SDKM,RN,RN2,BF
      INTEGER IST(3),IHEP,ID,IM,I,JHEP,LHEP,MHEP,NPR,MTRY,NTRY,IS
      EXTERNAL HWRGEN
      DATA IST/113,114,114/
      IF (IERROR.NE.0) RETURN
      IF(.NOT.RPARTY) THEN
        NHEP = NHEP+1
        ISTHEP(NHEP) = 3
        IDHW(NHEP) = 20
        IDHEP(NHEP) = 0
        CALL HWVEQU(5,PHEP(1,IHEP),PHEP(1,NHEP))
        CALL HWVEQU(4,VHEP(1,IHEP),VHEP(1,NHEP))
        JMOHEP(1,NHEP)=JMOHEP(1,IHEP)
        JMOHEP(2,NHEP)=JMOHEP(2,IHEP)
        JDAHEP(1,NHEP)=JDAHEP(1,IHEP)
        JDAHEP(2,NHEP)=JDAHEP(2,IHEP)
      ENDIF
C Make a copy of decaying object
      NHEP=NHEP+1
      ISTHEP(NHEP)=155
      IDHW(NHEP)=IDHW(IHEP)
      IDHEP(NHEP)=IDHEP(IHEP)
      CALL HWVEQU(5,PHEP(1,IHEP),PHEP(1,NHEP))
      CALL HWVEQU(4,VHEP(1,IHEP),VHEP(1,NHEP))
      JMOHEP(1,NHEP)=JMOHEP(1,IHEP)
      JMOHEP(2,NHEP)=JMOHEP(2,IHEP)
C--copy the location of the particle in the spin block
      IF(SYSPIN.AND.NSPN.NE.0) THEN
         IF(ISNHEP(IHEP).EQ.0) THEN
           IS = IHEP
           MTRY = 0
 5         MTRY = MTRY+1
           IS = JMOHEP(1,IS)
           IF(ISNHEP(IS).EQ.0.AND.MTRY.LE.NETRY) GOTO 5
           IF(MTRY.GT.NETRY) CALL HWWARN('HWDHO1',102,*999)
           ISNHEP(IHEP) = ISNHEP(IS)
         ENDIF
         ISNHEP(NHEP) = ISNHEP(JMOHEP(1,NHEP))
      ENDIF
      MTRY=0
 15   MTRY=MTRY+1
C Select decay mode
      RN=HWRGEN(0)
C **sh** changes to increase efficiency
C        nevertheless compliant with manual
      IF (MODBOS(1).EQ.5.AND.MODBOS(2).EQ.0) THEN
C allow only at least semileptonic decays 
         IF (ID.EQ.6.OR.ID.EQ.12) THEN
C decide first whether W+ or W- should decay leptonically           
            IF (NMULTI.EQ.1) THEN
               RN2=HWRGEN(0)
               IF (RN2.GE.0.5D0) THEN 
                  NMULTI=2
                  GOTO 22
               ELSE
                  GOTO 21
               ENDIF
            ELSEIF (NMULTI.EQ.2) THEN
C let W boson decay into e or mu
 21            NMULTI=0
               RN=0.778D0+(RN*0.222D0)
C modify weigth accordingly
               EVWGT=EVWGT*(1.-(1.-0.222D0)**2)
               GOTO 25
            ELSEIF (NMULTI.EQ.0) THEN
 22            RN2=HWRGEN(0)
               IF (RN2.LE.0.87514D0) THEN
                  RN=RN*0.778D0
               ELSE
                  RN=0.778D0+(RN*0.222D0)
               ENDIF
               GOTO 25
            ENDIF
         ELSE
            GOTO 25
         ENDIF
      ELSEIF (MODBOS(1).EQ.5.AND.MODBOS(2).EQ.5) THEN
C let W boson decay into e or mu 
         IF (ID.EQ.6.OR.ID.EQ.12) THEN
            RN=0.778D0+(RN*0.222D0)
C modify weigth accordingly
            EVWGT=EVWGT*(0.222D0**2)
            GOTO 25
         ELSE
            GOTO 25
         ENDIF
      ELSE
 25      BF=0.
         IM=LSTRT(ID)
         DO 20 I=1,NMODES(ID)
         BF=BF+BRFRAC(IM)
         IF (BF.GE.RN) GOTO 30
 20      IM=LNEXT(IM)
         CALL HWWARN('HWDHO1',50,*30)
      ENDIF
C **sh** original version
C      BF=0.
C      IM=LSTRT(ID)
C      DO 20 I=1,NMODES(ID)
C      BF=BF+BRFRAC(IM)
C      IF (BF.GE.RN) GOTO 30
C  20  IM=LNEXT(IM)
C      CALL HWWARN('HWDHO1',50,*30)
C **sh** ends here
  30  IF (NHEP+5.GT.NMXHEP) CALL HWWARN('HWDHO1',100,*999)
      NPR=NPRODS(IM)
      JDAHEP(1,NHEP)=NHEP+1
      JDAHEP(2,NHEP)=NHEP+NPR
C Reset colour pointers (if set)
      JHEP=JMOHEP(2,IHEP)
      IF (JHEP.GT.0) THEN
        IF (JDAHEP(2,JHEP).EQ.IHEP) JDAHEP(2,JHEP)=NHEP
        IF(.NOT.RPARTY.AND.ISTHEP(JHEP).EQ.155
     &    .AND.ABS(IDHEP(JHEP)).GT.1000000
     &    .AND.JDAHEP(2,JHEP-1).EQ.IHEP) JDAHEP(2,JHEP-1) = NHEP
      ENDIF
      JHEP=JDAHEP(2,IHEP)
      IF (JHEP.GT.0) THEN
        IF (JMOHEP(2,JHEP).EQ.IHEP) JMOHEP(2,JHEP)=NHEP
        IF(.NOT.RPARTY.AND.ISTHEP(JHEP).EQ.155
     &    .AND.ABS(IDHEP(JHEP)).GT.1000000
     &    .AND.JMOHEP(2,JHEP-1).EQ.IHEP) JMOHEP(2,JHEP-1) = NHEP
      ENDIF
C--Reset colour pointers if baryon number violated
      IF(.NOT.RPARTY) THEN
        DO JHEP=1,NHEP
          IF(ISTHEP(JHEP).EQ.155
     &       .AND.ABS(IDHEP(JHEP)).GT.1000000.AND.
     &       JDAHEP(2,JHEP-1).EQ.IHEP) JDAHEP(2,JHEP-1)= NHEP
          IF(JDAHEP(2,JHEP).EQ.IHEP) JDAHEP(2,JHEP)=NHEP
          IF(JMOHEP(2,JHEP).EQ.IHEP) JMOHEP(2,JHEP)=NHEP
        ENDDO
        IF(HRDCOL(1,1).EQ.IHEP) HRDCOL(1,1)=NHEP
      ENDIF
C Relabel original track
      IF (ISTHEP(IHEP).NE.120) ISTHEP(IHEP)=3
      JMOHEP(2,IHEP)=JMOHEP(1,IHEP)
      JDAHEP(1,IHEP)=NHEP
      JDAHEP(2,IHEP)=NHEP
C Label decay products and choose masses
      LHEP=NHEP
      MHEP=LHEP+1
      NTRY=0
 35   NTRY=NTRY+1
      SDKM=PHEP(5,NHEP)
      DO 40 I=1,NPR
      NHEP=NHEP+1
      IDHW(NHEP)=IDKPRD(I,IM)
      IDHEP(NHEP)=IDPDG(IDKPRD(I,IM))
      ISTHEP(NHEP)=IST(I)
      JMOHEP(1,NHEP)=LHEP
      JDAHEP(1,NHEP)=0
      PHEP(5,NHEP)=HWUMBW(IDKPRD(I,IM))
 40   SDKM=SDKM-PHEP(5,NHEP)
      IF (SDKM.LT.ZERO) THEN
        NHEP=NHEP-NPR
        IF (NTRY.LE.NETRY) GO TO 35
        CALL HWWARN('HWDHO1',1,*45)
 45     IF (MTRY.LE.NETRY) GO TO 15
        CALL HWWARN('HWDHO1',101,*999)
      ENDIF
C Assign production vertices to decay products
      CALL HWUDKL(ID,PHEP(1,IHEP),VHEP(1,MHEP))
      CALL HWVSUM(4,VHEP(1,IHEP),VHEP(1,MHEP),VHEP(1,MHEP))
      CALL HWVEQU(4,VHEP(1,MHEP),VHEP(1,NHEP))
 999  END
